-- what is  the total revenue generated by male vs female customer
select gender, sum(purchase_amount) as revenue
from customer
group by gender
-- which customers used a discount but still spent more than average purchase amount
select customer_id, purchase_amount
from customer
where discount_applied ='Yes' and purchase_amount >=(select avg(purchase_amount) from customer)
 
--top 5 product with highest review rating

select item_purchased,avg(review_rating) as "Average Product Rating" 
from customer 
group by item_purchased 
order by avg(review_rating) desc 
limit 5;
--compare the avg purchase amounts between standard and express shipping 
select round(avg(purchase_amount),2) as "Average Amount" ,shipping_type 
from customer 
where shipping_type in ('Standard','Express')
group by shipping_type 
--

--do subscribed customer spend more? compare average spend and total revenue 
-- betweem subscribers and non subscribers.

select subscription_status ,
count(customer_id) as total_cus,
round(avg(purchase_amount),2) as avg_spend,
round(sum(purchase_amount),2) as total_revenue
from customer 
group by subscription_status
order by total_revenue, avg_spend desc;

-- which 5 products have the highest percentage of purchase with discounts applied

select item_purchased, 
ROUND(100* sum(case when discount_applied='Yes' then 1 else 0 end)/count(*),2 ) as discount_rate
from customer
group by item_purchased 
order by discount_rate desc
limit 5;

--segment customers into new , returning and loyal
--based on purcash , count of each segment
with customer_type as (
select customer_id, previous_purchases,
case
when previous_purchases =1 then 'New'
when previous_purchases between 2 and 10 then 'Returning'
else 'Loyal'
end as customer_segment
from customer
)

select customer_segment , count(*) as "Number of Customers"
from customer_type
group by customer_segment


-- what are the top 3 most purchsed products within each category
with item_counts as (
select category,
item_purchased,
count(customer_id) as total_orders,
row_number() over(partition by category order by count(customer_id) desc)as item_rank
from  customer
group by category , item_purchased
)

select item_rank, category , item_purchased, total_orders
from item_counts
where item_rank<=3;

-- are customers who are repeat buyers (more than 5 previous purchases ) also likely to subscribe

select subscription_status,
count(customer_id) as repeat_buyers
from customer
where previous_purchases>5
group by subscription_status

-- what is each age group contribution to revenue

select age_group,
sum(purchase_amount) as revenue
from customer
group by age_group
order by revenue desc
